import React, { useState, useEffect, useRef } from 'react';
import { CSSProperties } from 'react';

const ChatBox: React.FC = () => {
  const [isExpanded, setIsExpanded] = useState(true);
  const [messages, setMessages] = useState<string[]>([]);
  const [input, setInput] = useState('');
  const chatLogRef = useRef<HTMLDivElement>(null);

  const handleToggle = () => {
    setIsExpanded(!isExpanded);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setMessages([...messages, input]);
    setInput('');
  };

  useEffect(() => {
    if (chatLogRef.current) {
      chatLogRef.current.scrollTop = chatLogRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <div style={styles.chatBox}>
      <button onClick={handleToggle} style={styles.toggleButton}>
        {isExpanded ? 'Collapse' : 'Expand'}
      </button>
      {isExpanded && (
        <div style={styles.chatLog} ref={chatLogRef}>
          {messages.map((msg, index) => (
            <div key={index} style={styles.message}>
              {msg}
            </div>
          ))}
        </div>
      )}
      <form onSubmit={handleSubmit} style={styles.form}>
        <textarea
          value={input}
          onChange={(e) => setInput(e.target.value)}
          style={styles.input}
          rows={2}
        />
        <button type="submit" style={styles.submitButton}>Send</button>
      </form>
      <div style={styles.buttonPanel}>
        <button style={styles.panelButton}>Terminate Process</button>
        <button style={styles.panelButton}>Clear Chat</button>
        <button style={styles.panelButton}>Extrapolate</button>
        <button style={styles.panelButton}>Export</button>
      </div>
    </div>
  );
};

const styles: { [key: string]: CSSProperties } = {
  chatBox: {
    display: 'flex',
    flexDirection: 'column',
    flexGrow: 1,
    backgroundColor: 'black',
    border: '1px solid #ccc',
  },
  toggleButton: {
    alignSelf: 'flex-start',
    color: 'white',
  },
  chatLog: {
    flexGrow: 1,
    overflowY: 'auto', // Allow scrolling within the chat log
    maxHeight: 'calc(100vh - 300px)', // Fixed height for the chat log
    backgroundColor: 'lightgrey',
    borderBottom: '1px solid #ccc',
  },
  message: {
    padding: '5px',
    borderBottom: '1px solid #eee',
    color: 'black',
  },
  form: {
    display: 'flex',
    borderTop: '1px solid #ccc',
  },
  input: {
    flexGrow: 1,
    padding: '5px',
    resize: 'none', // Prevent resizing
    marginRight: '10px',
  },
  submitButton: {
    padding: '5px 10px',
  },
  buttonPanel: {
    display: 'flex',
    justifyContent: 'space-around',
    backgroundColor: 'red',
    padding: '10px',
  },
  panelButton: {
    backgroundColor: '#61dafb',
    border: 'none',
    padding: '10px',
    cursor: 'pointer',
    color: 'white',
  },
};

export default ChatBox;
