import React, { useState, useEffect } from 'react';

interface HeaderProps {
  mode: string;
  setMode: (mode: string) => void;
}

const Header: React.FC<HeaderProps> = ({ mode, setMode }) => {
  const [time, setTime] = useState(new Date());
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  const handleModeChange = (newMode: string) => {
    setMode(newMode);
    setIsDropdownOpen(false);
  };

  return (
    <header style={styles.header}>
      <h1 style={styles.title}>PAL-Imperator</h1>
      <div style={styles.rightSection}>
        <div style={styles.modeSelector}>
          <button onClick={() => setIsDropdownOpen(!isDropdownOpen)} style={styles.modeButton}>
            {mode}
          </button>
          {isDropdownOpen && (
            <div style={styles.dropdown}>
              {['Music', 'Literature', 'Other'].map((m) => (
                <div key={m} onClick={() => handleModeChange(m)} style={styles.dropdownItem}>
                  {m}
                </div>
              ))}
            </div>
          )}
        </div>
        <div style={styles.clock}>{time.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</div>
      </div>
    </header>
  );
};

const styles = {
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '10px 20px',
    backgroundColor: '#282c34',
    color: 'white',
  },
  title: {
    margin: 0,
  },
  rightSection: {
    display: 'flex',
    alignItems: 'center',
  },
  modeSelector: {
    position: 'relative',
    marginRight: '20px',
  },
  modeButton: {
    backgroundColor: '#61dafb',
    border: 'none',
    padding: '10px',
    cursor: 'pointer',
  },
  dropdown: {
    position: 'absolute',
    top: '100%',
    left: 0,
    backgroundColor: '#fff',
    border: '1px solid #ccc',
    boxShadow: '0 2px 10px rgba(0,0,0,0.2)',
    zIndex: 1000,
  },
  dropdownItem: {
    padding: '10px',
    cursor: 'pointer',
  },
  clock: {
    fontSize: '1.2em',
  },
};

export default Header;
