import React, { useState } from 'react';
import Header from './components/Header';
import ChatBox from './components/ChatBox';
import { CommandInterface } from './components/CommandInterface';
import { DocumentDropBox } from './components/DocumentDropBox';
import { CSSProperties } from 'react';

const App: React.FC = () => {
  const [mode, setMode] = useState<'Music' | 'Literature' | 'Other'>('Music');

  return (
    <div className="App" style={styles.app}>
      <div style={styles.sidebar}>
        <div style={styles.titleBox}>
          <h1 style={styles.title}>PAL-Imperator</h1>
          <div style={styles.speakerName}>Augustus</div>
        </div>
        <ChatBox />
      </div>
      <div style={styles.mainContent}>
        <Header mode={mode} setMode={setMode} />
        <div style={styles.content}>
          <DocumentDropBox mode={mode} />
          <CommandInterface mode={mode} />
        </div>
      </div>
    </div>
  );
};

const styles: { [key: string]: CSSProperties } = {
  app: {
    display: 'flex',
    height: '100vh',
  },
  sidebar: {
    display: 'flex',
    flexDirection: 'column',
    width: '300px', // Assuming the chat box width is 300px
    backgroundColor: 'white',
    color: 'black',
    padding: '10px',
    boxShadow: '2px 0 5px rgba(0,0,0,0.1)',
  },
  titleBox: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor: 'red',
    padding: '10px',
    marginBottom: '20px',
  },
  title: {
    margin: 0,
    color: 'black',
  },
  speakerName: {
    fontSize: '1.5em',
    marginTop: '5px',
    color: 'white',
  },
  mainContent: {
    display: 'flex',
    flexDirection: 'column',
    flexGrow: 1,
  },
  content: {
    flexGrow: 1,
    padding: '20px',
  },
};

export default App;
