import React, { useState, useEffect, CSSProperties } from 'react';
import Header from './components/Header';
import ChatBox from './components/ChatBox';
import { CommandInterface } from './components/CommandInterface';
import { DocumentDropBox } from './components/DocumentDropBox';

const App: React.FC = () => {
  const [mode, setMode] = useState<'Music' | 'Literature' | 'Other'>('Music');
  const [theme, setTheme] = useState<'light' | 'dark'>('light');
  const [file, setFile] = useState<File | null>(null);

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') as 'light' | 'dark';
    if (savedTheme) {
      setTheme(savedTheme);
    }
  }, []);

  useEffect(() => {
    document.body.className = theme;
    localStorage.setItem('theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  const handleFileUpload = (uploadedFile: File) => {
    setFile(uploadedFile);
  };

  return (
    <div className="App" style={styles.app}>
      <div style={styles.sidebar}>
        <div style={styles.titleBox}>
          <h1 style={styles.title}>PAL-Imperator</h1>
          <div style={styles.speakerName}>Augustus</div>
        </div>
        <ChatBox />
      </div>
      <div style={styles.mainContent}>
        <Header mode={mode} setMode={setMode} theme={theme} toggleTheme={toggleTheme} />
        <div style={styles.content}>
          <DocumentDropBox mode={mode} onFileUpload={handleFileUpload} />
          <CommandInterface mode={mode} file={file} />
        </div>
      </div>
    </div>
  );
};

const styles: { [key: string]: CSSProperties } = {
  app: {
    display: 'flex',
    height: '100vh',
    overflow: 'hidden', // Prevent scrolling
    margin: '0', // Remove any default margin to prevent slack
  },
  sidebar: {
    display: 'flex',
    flexDirection: 'column',
    width: '300px',
    backgroundColor: 'black',
    color: 'white',
    padding: '10px',
    boxShadow: '2px 0 5px rgba(0,0,0,0.1)',
    height: '100vh',
    border: '2px solid red', // Red outline for the sidebar
    boxSizing: 'border-box', // Include border in height calculation
  },
  titleBox: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor: 'red',
    padding: '10px',
    marginBottom: '20px',
  },
  title: {
    margin: 0,
    color: 'black',
  },
  speakerName: {
    fontSize: '1.5em',
    marginTop: '5px',
    color: 'white',
  },
  mainContent: {
    display: 'flex',
    flexDirection: 'column',
    flexGrow: 1,
    height: '100vh',
  },
  content: {
    display: 'flex',
    flexDirection: 'column',
    flexGrow: 1,
    padding: '20px',
    overflow: 'auto', // Allow content to scroll within its container
  },
};

export default App;
